mapper.select:
SELECT tbgrant.id
FROM tbgrant
WHERE tbgrant.id is not NULL
AND tbgrant.deleted = #{deleted}
AND tbgrant.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbgrant.created_at
AND tbgrant.title LIKE CONCAT('%', #{title}, '%')
AND tbgrant.cate = #{cate}
ORDER BY tbgrant.created_at DESC;

SELECT tbgrant.id FROM tbgrant WHERE tbgrant.id is not NULL AND tbgrant.deleted = #{deleted} AND tbgrant.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbgrant.created_at AND tbgrant.title LIKE CONCAT('%', #{title}, '%') AND tbgrant.cate = #{cate}
AND STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f') > tbgrant.created_at ORDER BY tbgrant.created_at DESC
AND tbgrant.created_at > STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY tbgrant.created_at ASC
AND #{cursor} > tbgrant.title ORDER BY tbgrant.created_at DESC
AND tbgrant.title > #{cursor} ORDER BY tbgrant.created_at ASC
LIMIT 0, #{perpage}
