SELECT tbqnafile.id
FROM tbqnafile
WHERE tbqnafile.id is not NULL
AND tbqnafile.deleted = #{deleted}
AND tbqnafile.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000')
AND CONCAT(#{fdate}, ' 23:59:59.999999') >= tbqnafile.created_at
AND tbqnafile.tbqna_id = #{tbqnaId}
AND tbqnafile.cate = #{cate}
ORDER BY tbqnafile.created_at DESC;

